// Стилизация регионов для GeoJSON
const regionStyle = (feature) => {
  const value = feature?.properties?.value || 0;
  return {
    fillColor: getColor(value),
    weight: 2,
    opacity: 1,
    color: 'white',
    dashArray: '3',
    fillOpacity: 0.7
  };
};

// Цвет в зависимости от значения
const getColor = () => {
  const value = Math.floor(Math.random() * 1101);
  return value > 1000 ? '#800026' :
    value > 500 ? '#BD0026' :
      value > 200 ? '#E31A1C' :
        value > 100 ? '#FC4E2A' :
          value > 50 ? '#FD8D3C' :
            value > 20 ? '#FEB24C' :
              value > 10 ? '#FED976' :
                '#FFEDA0';
};

// Обработчик клика для GeoJSON
const onEachFeature = (feature, layer) => {
  if (feature.properties) {
    layer.bindPopup(`
      <div style="padding: 10px;">
        <strong style="font-size: 16px;">${feature.properties.region}</strong>
      </div>
    `);
  }

  layer.on({
    mouseover: (e) => {
      e.target.setStyle({
        weight: 3,
        color: '#666',
        dashArray: '',
        fillOpacity: 0.9
      });
      e.target.bringToFront();
    },
    mouseout: (e) => {
      e.target.setStyle({
        weight: 2,
        color: 'white',
        dashArray: '3',
        fillOpacity: 0.7
      });
    }
  });
};

